public class RoomInfoBean {
 private int id;
 private int floor;
 private RoomStateBean roomState;
 private RoomTypeBean roomType;
  
public RoomInfoBean() {
	super();
	// TODO Auto-generated constructor stub
}
public int getId() {
	return id;
}
public void setId(int id) {
	this.id = id;
}
public int getFloor() {
	return floor;
}
public void setFloor(int floor) {
	this.floor = floor;
}
public RoomStateBean getRoomState() {
	return roomState;
}
public void setRoomState(RoomStateBean roomState) {
	this.roomState = roomState;
}
public RoomTypeBean getRoomType() {
	return roomType;
}
public void setRoomType(RoomTypeBean roomType) {
	this.roomType = roomType;
}
@Override
public String toString() {
	return "RoomInfoBean [id=" + id + ", floor=" + floor + ", roomState=" + roomState + ", roomType=" + roomType + "]";
}

 
	

}
